USE [KINYO-LIVE]SELECT DISTINCT	T1.DocNum			AS	'Invoice/Credit Num',	--T8.DocNum			AS  'OrderNum',	T1.DocDate			AS	'InvDate',	--T8.DocDate			AS  'OrderDate',	T3.CardName			AS  'Customer',	T3.CardCode			AS	'Customer Code',	T4.SlpName          AS  'Sales Employee',	T1.ShipToCode,	T5.CityS,	T5.StateS,	T1.NumAtCard		AS  'CustPONum',	T6.LineNum,	T6.ItemCode,	T7.FrgnName			AS  'Base Itemcode',	T7.ItemName,	--CASE	--	WHEN T3.QryGroup1='Y' THEN (SELECT T0.U_Length FROM INV1 T0 WHERE T0.DocEntry=T1.DocEntry AND T0.LineNum=T6.LineNum AND T0.ItemCode=T6.ItemCode )	--	WHEN T3.QryGroup1='N' THEN (SELECT T0.SLength1 FROM OITM T0 WHERE T0.ItemCode=T6.ItemCode )	--END 'Around',	--CASE	--	WHEN T3.QryGroup1='Y' THEN (SELECT T0.U_Width FROM INV1 T0 WHERE T0.DocEntry=T1.DocEntry AND T0.LineNum=T6.LineNum AND T0.ItemCode=T6.ItemCode )	--	WHEN T3.QryGroup1='N' THEN (SELECT T0.SWidth1 FROM OITM T0 WHERE T0.ItemCode=T6.ItemCode )	--END 'Across',	--CASE	--	WHEN T3.QryGroup1='Y' THEN (SELECT T0.U_Gauge FROM INV1 T0 WHERE T0.DocEntry=T1.DocEntry AND T0.LineNum=T6.LineNum AND T0.ItemCode=T6.ItemCode )	--	WHEN T3.QryGroup1='N' THEN (SELECT T0.SHeight1 FROM OITM T0 WHERE T0.ItemCode=T6.ItemCode )	--END 'Gauge',	T6.U_Ship_Quantity	AS	'Pieces',	T6.Quantity,	T6.Price,	T6.LineTotalFROM DLN1 T0    WITH (NOLOCK)	RIGHT OUTER JOIN OINV  T1	ON T1.DocEntry=T0.TrgetEntry  	LEFT  OUTER JOIN RDR1  T2	ON T2.DocEntry=T0.BaseEntry	LEFT  OUTER JOIN OCRD  T3   ON T3.CardCode=T1.CardCode	LEFT  OUTER JOIN OSLP  T4	ON T4.SlpCode=T1.SlpCode	LEFT  OUTER JOIN INV12 T5	ON T5.DocEntry=T1.DocEntry	LEFT  OUTER JOIN INV1  T6   ON T6.DocEntry=T1.DocEntry	LEFT  OUTER JOIN OITM  T7   ON T7.ItemCode=T6.ItemCode	--LEFT  OUTER JOIN ORDR  T8   ON T8.DocEntry=T0.BaseEntry AND T8.DocNum=T0.BaseRefWHERE 	T1.DocDate >='2021-01-02 00:00:00'	AND	T1.DocDate <='2021-01-31 00:00:00'	AND	T1.CANCELED='N'		AND T3.QryGroup1='N'	--AND T1.DocNum=700289ORDER BY 	T1.DocNum		----SELECT * FROM ODLN WHERE DocNum=600024--SELECT * FROM DLN1 WHERE DocEntry=3073--SELECT * FROM ORDR WHERE DocNum=505613--SELECT * FROM RDR1 WHERE DocEntry=2329